class MarvelService {
  getResource = async (url) => {
    let res = await fetch(url);

    if (!res.ok) {
      throw new Error(`Could not fetch ${url}, status: ${res.status}`);
    }
    return await res.json();
  }

  getAllCharacters = () => {
    return this.getResource('https://gateway.marvel.com:443/v1/public/characters?limit=9&offset=210&apikey=e857b55234347cfb7f6425f75329ee7f');
  }

  getCharacter = (id) => {
    return this.getResource(`https://gateway.marvel.com:443/v1/public/characters/${id}?apikey=e857b55234347cfb7f6425f75329ee7f`);
  }
}

export default MarvelService;
