import {useHttp} from '../hooks/http.hook'

const useMarvelService = () => {
  const {loading, request, error, clearError} = useHttp()

  const _apiBase = 'https://gateway.marvel.com:443/v1/public/';
  const _apiKey = 'apikey=e857b55234347cfb7f6425f75329ee7f';
  const _baseOffset = 210;

  const getAllCharacters = async (offset = _baseOffset) => {
    const res = await request(`${_apiBase}characters?limit=9&offset=${offset}&${_apiKey}`);
    return res.data.results.map(_transformCharacter);
  }

  const getCharacter = async (id) => {
    const res = await request(`${_apiBase}characters/${id}?${_apiKey}`);
    return _transformCharacter(res.data.results[0]);
  }

  const getAllComics = async () => {
    const res = await request(`${_apiBase}comics?limit=8&${_apiKey}`);
    return _transformComic(res.data.results);
  }


  const _transformCharacter = (char) => {
    return {
      id: char.id,
      name: char.name,
      description: char.description ? `${char.description.slice(0, 210)}...` : 'Для этого персонажа нет описания',
      thumbnail: char.thumbnail.path + '.' + char.thumbnail.extension,
      homepage: char.urls[0].url,
      wiki: char.urls[1].url,
      comics: char.comics.items
    }
  }

  const _transformComic = (comic) => {
    return {
      id: comic.id,
      title: comic.title,
      // price: comic.prices[0].price,
      thumbnail: comic.thumbnail.path + '.' + comic.thumbnail.extension
    }
  }

  return {loading, error, clearError, getAllCharacters, getCharacter, getAllComics}
}

export default useMarvelService;
